"use client"
import React, { useState, useEffect } from "react";
import HeaderTop from "./headerTop";
import NavigationMenu from "./navigationMenu";

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <header
        className={`fixed top-0 left-0 w-full z-50 transition-all duration-300 ${
          isScrolled ? "bg-white text-black shadow-md" : "bg-transparent text-white"
        }`}
      >
        {/* <HeaderTop isScrolled = {isScrolled}/> */}
        <div className="container">
          <div className="header-wrapper flex items-center justify-between pb-2">
            {/* Logo */}
            <a className="logo" href="/">
            {isScrolled ? <img
              src="https://www.nextwebi.com/assets/images/nextwebi-logo.png"
              alt="Logo"
              srcSet=""
              className="pl-15 w-65"
            />
            :
            <img
              src="/assets/logo-white.png"
              alt="Logo"
              srcSet=""
              className="pl-15 w-65"
            />
          }
          </a>

            {/* Navigation */}
            {/* <NavigationMenu isScrolled = {isScrolled}/> */}

            {/* Button */}
            <div className="right-button hidden lg:block md:w-50">
              <a
                href="/"
                className={`btn py-2 px-4 rounded-xl transition-all bg-orange-600 text-white hover:bg-orange-800`}
              >
                Get a Quote
              </a>
            </div>
          </div>
        </div>
      </header>

      
    </>
  );
};

export default Header;
