import { useState } from "react";
import { Menu, X, ChevronDown } from "lucide-react";
import MegaMenu from "./MegaMenu";
import ExpandableMenu from "./expandableMenu";

const NavigationMenu = ({ isScrolled }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [openMenu, setOpenMenu] = useState(null);
  const [openMobileMenu, setOpenMobileMenu] = useState(null);

  const menuItems = [
    { name: "Company", content: <MegaMenu /> },
    { name: "Services", content: <ExpandableMenu /> },
    { name: "Hire Developers", content: <p className="text-gray-700 p-4">Hire Developers Content</p> },
    { name: "Solutions", content: <p className="text-gray-700 p-4">Solutions Content</p> },
    { name: "Technologies", content: <p className="text-gray-700 p-4">Technologies Content</p> },
  ];

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav className={`${isScrolled ? "text-gray-900" : "text-gray-100"} py-4`}>
      <div className="container mx-auto flex justify-between items-center">
        <div className="relative hidden md:flex space-x-6">
          {menuItems.map((item, index) => (
            <div
              key={index}
              className="relative group"
              onMouseEnter={() => setOpenMenu(index)}
              onMouseLeave={() => setOpenMenu(null)}
            >
              <a href="#" className="flex items-center space-x-1 hover:text-gray-400">
                <span>{item.name}</span>
                <ChevronDown size={16} />
              </a>

              {/* Dropdown Content */}
              {openMenu === index && (
                <div
                  className="absolute left-0 w-auto bg-white shadow-lg border rounded-lg"
                  onMouseEnter={() => setOpenMenu(index)}
                  onMouseLeave={() => setOpenMenu(null)}
                >
                  {item.content}
                </div>
              )}
            </div>
          ))}
          <a href="#" className="hover:text-gray-400">Our Work</a>
        </div>
          
        <button className="md:hidden" onClick={toggleMenu}>
          {isOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {isOpen && (
        <ul className="md:hidden mt-2 bg-gray-800 p-4 space-y-2">
          {menuItems.map((item, index) => (
            <li key={index}>
              <button
                onClick={() => setOpenMobileMenu(openMobileMenu === index ? null : index)}
                className="flex justify-between items-center w-full text-white hover:text-gray-400"
              >
                <span>{item.name}</span>
                <ChevronDown size={16} className={`${openMobileMenu === index ? "rotate-180" : ""}`} />
              </button>

              {openMobileMenu === index && (
                <div className="bg-white text-gray-900 p-4 mt-2 rounded-lg shadow">
                  {item.content}
                </div>
              )}
            </li>
          ))}
          <li>
            <a href="#" className="block text-white hover:text-gray-400">Our Work</a>
          </li>
        </ul>
      )}
    </nav>
  );
};

export default NavigationMenu;
