import React, { useState } from "react";
import { ChevronDown, ChevronUp } from "lucide-react";

function Accordion() {
  const [openIndex, setOpenIndex] = useState(null);

  // Accordion data declared inside the file
  const items = [
    {
      heading: "Self-Insured Claims Management",
      description:
        "Manage your claims efficiently with tailored self-insured programs.",
    },
    {
      heading: "Risk Management Insights",
      description:
        "Leverage data-driven insights to reduce liability and exposure.",
    },
    {
      heading: "Client Support Services",
      description:
        "Access 24/7 support and dedicated case handlers for every client.",
    },
  ];

  const toggleAccordion = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <div className="w-full max-w-6xl mx-auto">
      {items.map((item, index) => {
        const isOpen = openIndex === index;
        return (
          <div key={index} className="mb-4 border-b border-gray-300">
            <div className="text-xl font-semibold text-gray-800">
              <button
                onClick={() => toggleAccordion(index)}
                className="w-full flex items-center justify-between py-3 px-4 text-left text-gray-700 hover:text-blue-600 transition-colors duration-300"
                aria-expanded={isOpen}
                aria-controls={`accordion-${index}`}
              >
                <span>
                  <i className="not-italic">{item.heading}</i>
                </span>
                <span className="ml-2">
                  {isOpen ? (
                    <ChevronUp size={20} />
                  ) : (
                    <ChevronDown size={20} />
                  )}
                </span>
              </button>
            </div>
            <div
              id={`accordion-${index}`}
              className={`overflow-hidden transition-all duration-500 ease-in-out ${
                isOpen ? "max-h-96 opacity-100" : "max-h-0 opacity-0"
              }`}
              aria-hidden={!isOpen}
            >
              <div className="px-4 py-3 text-gray-600">
                {item.description}
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Accordion;
