import React from "react";

const Banner = () => {
    return (
        <div className="relative w-full h-[400px] md:h-[500px]">
            {/* Banner Image */}
            <img
                src="/assets/banner.png"
                alt="Banner"
                className="w-full h-full object-cover"
            />

            {/* Banner Content */}
            <div className="absolute inset-0 bg-transparent flex flex-col justify-start items-start text-left text-white pt-20 sm:p-16 md:p-12 lg:p-24">
                <p className="text-lg sm:text-xl md:text-2xl lg:text-3xl font-bold text-gray-100/80 mt-10 sm:pt:8">
                    DRIVING FUTURE INNOVATIONS
                </p>
                <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl mt-4">
                    AI-Enabled Solutions for
                </h1>
                <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold text-blue-400 mt-2">
                    Digital Transformations
                </h1>
                <button className="mt-8 md:mt-10 border border-white text-white px-6 py-3 md:px-8 md:py-4 backdrop-blur-lg rounded-lg hover:bg-white hover:text-black transition">
                    Schedule an Appointment
                </button>
            </div>
        </div>
    );
};

export default Banner;
