import React, { useState } from "react";
import { ChevronRight, ChevronDown } from "lucide-react"; // Icons for expand/collapse

const services = [
    {
        id: 1, title: "Software Dev", links: [
            { href: "/", text: "Offshore Dev" },
            { href: "/", text: "Custom Dev" },
            { href: "/", text: "Web App Dev" }
        ]
    },
    {
        id: 2, title: "Web Dev", links: [
            { href: "/", text: "E-commerce" },
            { href: "/", text: "React Dev" },
            { href: "/", text: "Next.js Dev" }
        ]
    },
    {
        id: 3, title: "IT Services", links: [
            { href: "/", text: "Cloud Consulting" },
            { href: "/", text: "Cyber Security" },
            { href: "/", text: "Tech Support" }
        ]
    }
];

const ExpandableMenu = () => {
    const [openMenu, setOpenMenu] = useState(null);

    const toggleMenu = (id) => {
        setOpenMenu(openMenu === id ? null : id);
    };

    return (
        <div className="w-40 bg-white shadow-lg rounded-md p-2">
            {services.map((service) => (
                <div
                    key={service.id}
                    className="relative group h-8"
                    onMouseEnter={() => setOpenMenu(service.id)}
                    onMouseLeave={() => setOpenMenu(null)}
                >
                    <button
                        onClick={() => toggleMenu(service.id)}
                        className="w-full flex justify-between items-center px-2 h-4 text-sm font-medium text-gray-800 hover:bg-gray-100"
                    >
                        {service.title}
                    </button>

                    {/* Submenu - Appears on the right */}
                    {openMenu === service.id && (
                        <div className="absolute top-0 left-full ml-1 w-40 bg-gray-50 border shadow-md rounded-md z-10">
                            {service.links.map((link, index) => (
                                <a
                                    key={index}
                                    href={link.href}
                                    className="block px-3 py-2 text-gray-700 text-xs hover:bg-gray-200"
                                >
                                    {link.text}
                                </a>
                            ))}
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default ExpandableMenu;
