import React, { useState } from "react";

const AccordionItem = () => {
  const [isOpen, setIsOpen] = useState(false);
  const accordionId = "accordion-card-77dd192c-cd90-474c-842a-afe9313e3cb5";

  const toggleAccordion = () => setIsOpen((prev) => !prev);

  return (
    <div className="mb-4 border-b border-gray-300">
      <div className="text-xl font-semibold text-gray-800">
        <button
          onClick={toggleAccordion}
          className={`w-full text-left py-3 px-4 transition-colors duration-300 ${
            isOpen ? "text-blue-600" : "text-gray-700"
          }`}
          aria-expanded={isOpen}
          aria-controls={accordionId}
        >
          <i className="not-italic">Self-Insured Claims Management</i>
        </button>
      </div>
      <div
        id={accordionId}
        className={`overflow-hidden transition-max-height duration-500 ease-in-out ${
          isOpen ? "max-h-96" : "max-h-0"
        }`}
        aria-hidden={!isOpen}
      >
        <div className="px-4 py-3 text-gray-600">
          {/* Replace this with actual accordion content */}
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed
          dignissim, nisl vel tincidunt fringilla, velit nulla pulvinar nisi.
        </div>
      </div>
    </div>
  );
};

export default AccordionItem;
